import { Component } from '@angular/core';

export interface Pony {
  id: number;
  name: string;
  img: string;
}

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})
export class ListadoComponent {
  title: string = 'Listado de Heroes';
  listPonys: Pony[] = [
    {
      id: 1,
      name: 'Applejack',
      img: 'https://pa1.narvii.com/6521/939c7d6b7e0781044f53a0aa36024c9dfc090949_hq.gif',
    },
    {
      id: 2,
      name: 'bFluttershy',
      img: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQm5meq7jpvs_xx6pxzDb0U0OV3KHzlm1x7kQ&usqp=CAU',
    },

    {
      id: 3,
      name: 'Rarity',
      img: 'data:image/jpeg;base64,',
    },
    {
      id: 4,
      name: 'Twilight Sparkle',
      img: 'data:image/jpeg;base64,',
    },
    {
      id: 5,
      name: 'Pinkie Pie',
      img: 'data:image/jpeg;base64,',
    },
  ];
  ponysDelete: Pony[] = [];

  transferirUltimoValor(arrayDesde: any[], arrayHasta: any[]) {
    const poppedElement = arrayDesde.pop();
    if (poppedElement) {
      arrayHasta.push(poppedElement);
    }
  }
  deletePony() {
    this.transferirUltimoValor(this.listPonys, this.ponysDelete);
  }
  addPony() {
    this.transferirUltimoValor(this.ponysDelete, this.listPonys);
  }
}
